const { BrowserWindow  } = require('electron');

module.exports = function showDictionary(themeName){
    var bgColor = themeName == 'light' ? '#FFFFFF' : '#282727'
    const win = new BrowserWindow({
        modal: false,
        height: 600,
        width: 1000,
        minWidth: 500,
        minHeight: 500,
        transparent: false,
        backgroundColor: bgColor,
        minimizable: true,
        maximizable: true,
        resizable: true,
        alwaysOnTop: false,
        fullscreen: false,
        fullscreenable: true,
        skipTaskbar: false,
        frame: true,
        show: true,
        icon: `${__dirname}/dist/favicon.ico`,
        title: "Dictionary",
        webPreferences: {
            nodeIntegration: true,
            webviewTag: true,
            contextIsolation: false,
            webSecurity: false,
            enableRemoteModule: true
        }
    });
    win.loadFile(`${__dirname}/dist/index.html`)
    return win
}