<template>
  <div id="app">
    <router-view v-slot="{ Component }">
      <transition name="fade" mode="out-in">
        <component :is="Component" />
      </transition>
    </router-view>
  </div>
</template>

<script>
import { useTheme } from 'vuetify'
import DatabaseUtil from './db/DatabaseUtil';
const { ipcRenderer } = window.require('electron')
const EStore = window.require('electron-store');
export default {
  name: 'App',
  mounted() {
    const estore = new EStore();
    const vueTheme = useTheme()
    DatabaseUtil.initDB();
    vueTheme.global.name.value = estore.get('theme') || 'dark';
    ipcRenderer.on("theme-change",(e,theme)=>{
        vueTheme.global.name.value = theme || 'dark';
    })
  }
}
</script>

<style>
#app {
  font-family: Avenir, Helvetica, Arial, sans-serif;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-align: center;
  color: #2c3e50;
}

.fade-enter-active,
.fade-leave-active {
  transition: opacity 0.3s ease-in-out;
}
.fade-enter,
.fade-leave-to {
  opacity: 0;
}
</style>
