import { createApp } from 'vue'
import App from './App.vue'
import Router from './router/index'
import Store from './store/index'
// Vuetify
import { createVuetify } from 'vuetify'
import * as components from 'vuetify/components'
import * as directives from 'vuetify/directives'
import { aliases, mdi } from 'vuetify/iconsets/mdi'
import VueVirtualScroller from 'vue-virtual-scroller'
import '@/assets/style/index.scss'
import 'vue-virtual-scroller/dist/vue-virtual-scroller.css'
import '@mdi/font/css/materialdesignicons.css'

const light = {
  dark: false,
  colors:{
    background: "#FFFFFF",
    surface: "#F4F5F6",
    primary: "#F4F5F6",
    secondary: "#FFFFFF",
    accent: '#EEEEEE',
    error: '#FF5252',
    info: '#2196F3',
    success: '#4CAF50',
    warning: '#FFC107',
    example: '#371FFF'
  }
}

const dark = {
  dark: true,
  colors:{
    background: "#282727",
    surface: "#121212",
    primary: "#121212",
    secondary: "#282727",
    accent: '#202124',
    error: '#FF5252',
    info: '#2196F3',
    success: '#4CAF50',
    warning: '#FFC107',
    example: '#9fa0ff'
  }
}
const vuetify = createVuetify({
  icons: {
    defaultSet: 'mdi',
    aliases,
    sets: {
      mdi,
    }
  },
  components,
  directives,
  theme: {
    defaultTheme: 'light',
    themes: {
      light,dark
    }
  }
})
createApp(App).use(vuetify).use(Router).use(Store).use(VueVirtualScroller).mount('#app')
