import { createStore } from 'vuex'

const store = createStore({
    state () {
      return {
        chounnatLexical: [],
        khmerDictionary: [],
        bookmarkList: []
      }
    },
    mutations: {
        setChounNatLexical (state, chounnatLexical) {
          state.chounnatLexical = chounnatLexical
        },
        setKhmerDictionary(state, khmerDictionary){
          state.khmerDictionary = khmerDictionary
        },
        setBookmarkList(state, bookmarkList){
          state.bookmarkList = bookmarkList
        } 
    }
  });

  export default store