const { BrowserWindow  } = require('electron');

module.exports = function showZoom(parent){
    const win = new BrowserWindow({
        modal: false,
        height: 600,
        width: 800,
        minWidth: 500,
        minHeight: 500,
        transparent: false,
        backgroundColor:"#000000",
        minimizable: true,
        maximizable: true,
        resizable: true,
        alwaysOnTop: false,
        fullscreen: false,
        fullscreenable: true,
        skipTaskbar: false,
        frame: true,
        show: false,
        icon: `${__dirname}/dist/favicon.ico`,
        title: "Conference",
        webPreferences: {
            nodeIntegration: true,
            webviewTag: true,
            contextIsolation: false,
            webSecurity: false,
            enableRemoteModule: true
        }
    });
    win.loadFile(`${__dirname}/dist/index.html`)
    //win.loadURL("http://localhost:8080/")
    return win
}