<template>
    <div class="setting">
        <div class="title">
            Session Settings
            <i class="right icon-close el-icon-close"></i>
        </div>
        <div class="text-subtitle-1 text-medium-emphasis">Camera</div>
        <v-select
            label="Select"
            v-model="selectCamera"
            :items="cameraList"
            item-title="label"
            item-value="deviceId"
            ></v-select>
        <div class="text-subtitle-1 text-medium-emphasis">Microphone</div>
        <v-select
            label="Select"
            :items="['California', 'Colorado', 'Florida', 'Georgia', 'Texas', 'Wyoming']"
            ></v-select>
    </div>
</template>

<script>
import ZoomController from '../../ZoomController';
export default {
    data(){
        return {
            selectCamera: null,
            cameraList: []
        }
    },
    watch:{
        selectCamera(val){
            console.log("select-dd",val)
            if(val){
                this.switchCamera(val)
            }
        }
    },
    mounted(){
        this.cameraList = ZoomController.zoomClient.getMediaStream().getCameraList()
        console.log("video-camera",ZoomController.zoomClient.getMediaStream().getCameraList())
    },
    methods:{
        async switchCamera(deviceId){
            await ZoomController.zoomClient.getMediaStream().switchCamera(deviceId)
            console.log("active", ZoomController.zoomClient.getMediaStream().getActiveCamera())
        }
    }
}
</script>

