<template>
    <div class="pagination">
      <!-- <Button
        key="left"
        class="page-button pre-page"
        :class="{hidden: !canGoBack}"
        @click="toPreviousPage"
      >
        Pre {{this.pageIndication}}
      </Button> -->
      <v-btn
        key="left"
        class="page-button pre-page"
        :class="{hidden: !canGoBack}"
        @click="toPreviousPage"
        icon="mdi-arrow-left-thick"
        color="secondary"
        ></v-btn>
      <!-- <Button
      class="page-button next-page"
        :class="{hidden: !canNextPage}"
        key="right" @click="toNextPage">
        Next {{this.pageIndication}}
      </Button> -->
      <v-btn
            icon="mdi-arrow-right-thick"
            color="secondary"
            class="page-button next-page"
            :class="{hidden: !canNextPage}"
            key="right" @click="toNextPage"
        ></v-btn>
    </div>
</template>

<script>
export default {
    name: "Pagination_d",
    props: {
        page: Number,
        totalPage: Number,
    },
    emits:["toPreviousPage","toNextPage"],
    data() {
        return {
            pageIndication: 0
        }
    },
    computed: {
        canNextPage(){
            return this.page < this.totalPage - 1
        },
        canGoBack(){
            return this.page > 0
        }
    },
    mounted() {
        this.pageIndication = `${this.page + 1}/${this.totalPage}`;
    },
    methods: {
        toPreviousPage(){
            console.log("Prev")
            this.$emit('toPreviousPage')
        },
        toNextPage(){
            console.log("Next")
            this.$emit('toNextPage')
        }
    },
}
</script>

