<template>
    <div class="container">
        <div class="btn-fullscreen" @click="onClickToggleFullScreen">
            <IconMeetingExitFullScreen v-if="isFullScreen"></IconMeetingExitFullScreen>
            <IconMeetingFullScreen v-else></IconMeetingFullScreen>
        </div>
    </div>
</template>
<script>
const currentWindow = window.require('@electron/remote').getCurrentWindow()
import IconMeetingFullScreen from './../Icon/IconMeetingFullScreen.vue'
import IconMeetingExitFullScreen from './../Icon/IconMeetingExitFullScreen.vue'
export default {
    components: {IconMeetingFullScreen,IconMeetingExitFullScreen},
    data(){
        return {
            isFullScreen: false
        }
    },
    mounted(){
        this.isFullScreen = currentWindow?.isFullScreen()
    },
    methods: {
        onClickToggleFullScreen(){
            currentWindow?.setFullScreen(!currentWindow?.isFullScreen())
            this.isFullScreen = currentWindow?.isFullScreen()
        }
    }
}
</script>
<style lang="scss" scoped>
    .container{
        padding-right: 3px;
        .btn-fullscreen{
            width: 35px;
            height: 35px;
            margin: 2px;
            display: inline-flex;
            justify-content: center;
            align-items: center;
            background: rgba(26, 26, 26, 0.8);
            border: 2px solid rgba(255, 255, 255, 0.09);
            border-radius: 6px;
            &:hover{
                background: rgba(255, 255, 255, 0.1);
            }
        }
    }
    
</style>