<template>
    <div class="main-meeting" :class="{hidden: !isMainView}" ref="mainMeetingRef">
      
      <MeetingShare :isShare="currentMainState=='share' && isMainView" :user="userShareScreen" />
      <MeetingUserPin :isPin="currentMainState=='pin' && isMainView" :user="userPin" />
      <MeetingUserActive :isActive="currentMainState=='active' && isMainView" :user="userActive" />

      <template v-if="mainReaction!=null"><EmojiToast :reaction="mainReaction" /></template>
      <div class="corner-state">
        <span v-if="currentUser?.bVideoOn" class="name">{{ currentUser?.displayName }}</span>
        <v-icon v-if="currentUser?.muted==true" icon="mdi-volume-mute" size="large" color="blue-darken-2"></v-icon>
        <v-icon v-if="currentUser?.bVideoOn==false" icon="mdi-video-off" size="large" color="blue-darken-2"></v-icon>
      </div>
      <span v-if="!currentUser?.bVideoOn && !userShareScreen" class="center-name">{{ currentUser?.displayName }}</span>
    </div>
</template>
<script>
import ZoomController from '../../ZoomController'
import EmojiToast from '../EmojiToast.vue'
import MeetingShare from './MeetingShare.vue'
import MeetingUserPin from './MeetingUserPin.vue'
import MeetingUserActive from './MeetingUserActive.vue'
import { getVideoLayout } from '../../utils/VideoLayoutHelper'
export default {
    components:{EmojiToast, MeetingShare, MeetingUserPin, MeetingUserActive},
    props:['users','isMainView'],
    data(){
        return{
            user: null,
            currentUserPin: null,
            currentUserShare: null,
            currentUserActive: null
        }
    },
    computed:{
      raisedHandMap () {
        //this.updateMeetingViewPorts()
        return this.$store.state.cmdRaisedHandMap
      },
      mainReaction(){
        return this.$store.state.reactionMain
      },
      reaction(){
        const key = Object.keys(this.$store.state.cmdReactionMap)[0]
        return this.$store.state.cmdReactionMap[key]
      },
      reactionMap () {
        //this.updateMeetingViewPorts()
        return this.$store.state.cmdReactionMap
      },
      userShareScreen(){
        return this.$store.state.userShareScreen
      },
      userActive(){
        return this.$store.state.userActive
      },
      userPin(){
        return this.$store.state.userPin
      },
      currentUser(){
        if(this.userShareScreen){
          return this.userShareScreen
        } else if(this.userPin){
          return this.userPin
        }else{
          return this.userActive
        }
      },
      currentMainState(){
        if(this.userShareScreen){
          return "share"
        }else if(this.userPin){
          return "pin"
        }else if(this.userActive){
          return "active"
        }else{
          return ""
        }
      }
    },
    watch:{
        
    },
    mounted() {
        
    },
    methods: {
      
    },
}
</script>
<style lang="scss" scoped>
    .main-meeting{
        width: 100%;
        height: 100%;
        display: flex;
        justify-content: center;
        align-items: center;
        &.hidden{
          display: none;
        }
        .canvas-active{
          width: 33%;
          height: 100%;
          float: right;
          &.hidden{
            display: none;
          }
        }
        .corner-state{
          position: absolute;
          top: 50px;
          right: 20px;
          .name{
            color: #fff;
          }
        }
        .center-name{
          position: absolute;
          font-size: 38px;
          font-weight: 700;
          color: #fff;
        }
    }
</style>