<template>
    <div class="main-pin-meeting" :class="{hidden: !isPin}">
        <canvas id="canvas-pin" class="canvas-pin" width="800" height="600" ref="pinRef" />
    </div>
</template>

<script>
import { debounce } from '../../assets/js/util'
import ZoomController from '../../ZoomController';
export default {
    props:['user','isPin'],
    data(){
        return {
            ro: null,
            activeVideo: false,
            currentUser: null
        }
    },
    computed:{
        
    },
    watch: {
        isPin(val){
            console.log("isPin",val,this.currentUser,this.user)
            let self = this
            if(val){
                this.currentUser = this.user
                setTimeout(() => {
                    self.onResize()
                }, 100);
            }else{
                if(this.currentUser){
                    ZoomController.zoomClient.getMediaStream().stopRenderVideo(this.$refs.pinRef, this.currentUser.userId,'pin') 
                }
            }
        },
        user(val){
            if(val){
                if(this.currentUser){
                    ZoomController.zoomClient.getMediaStream().stopRenderVideo(this.$refs.pinRef, this.currentUser.userId,'pin') 
                }
                if(val.bVideoOn){
                    this.onResize()
                }else{
                    ZoomController.zoomClient.getMediaStream().stopRenderVideo(this.$refs.pinRef, val.userId,'pin') 
                }
                this.currentUser = val
            }
        }
    },
    mounted(){
        console.log("MeetingUserPin-mounted", this.user)
        this.ro = new ResizeObserver(debounce(this.onResize,500))
        this.ro.observe(this.$refs.pinRef)
    },
    beforeUnmount(){
        this.ro.unobserve(this.$refs.pinRef)
        if(this.user){
           ZoomController.zoomClient.getMediaStream().stopRenderVideo(this.$refs.pinRef, this.user.userId,'pin') 
        }
    },
    unmounted(){
        console.log("MeetingUserPin-unmounted", this.user)
        if(this.user){
           ZoomController.zoomClient.getMediaStream().stopRenderVideo(this.$refs.pinRef, this.user.userId,'pin') 
        }
    },
    methods:{
        async onResize(){
            if(this.user && this.isPin){
                const width = this.$refs.pinRef.offsetWidth;
                const height = this.$refs.pinRef.offsetHeight;
                console.log("onResizeUserPin", width, height)
                await ZoomController.zoomClient.getMediaStream().stopRenderVideo(this.$refs.pinRef, this.user.userId,"pin")
                if(this.user?.bVideoOn == true){
                    await ZoomController.zoomClient.getMediaStream().renderVideo(this.$refs.pinRef, this.user.userId, width, height, 0, 0, 1,"pin")
                }
                ZoomController.zoomClient.getMediaStream().updateVideoCanvasDimension(this.$refs.pinRef, width, height)
            }
        },
        userData(){
            if(!this.user) return null
            return ZoomController.zoomClient.getUser(this.user.userId);
        }
    }
}
</script>

<style lang="scss" scoped>
    .main-pin-meeting{
        width: 100%;
        height: 100%;
        &.hidden{
            display: none;
        }
        .canvas-pin{
          width: 100%;
          height: 100%;
          float: left;
          &.hidden{
            display: none;
          }
        }
    }
</style>