<template>
    <div class="video-preview-container" :class="{selected: (activeId == item.id)}" @mouseover="onHover" @mouseleave="onLeave" @click="onSelect">
        <div class="video-preview-box">
            <video class="video-preview" :class="{hidden: !hover}" :srcObject="viewStream" autoplay="autoplay" ref="video" />
            <img class="image-preview" :class="{hidden: hover}" :src="item.url" />
        </div>
        <div class="title"><span>{{item?.name}}</span></div>
    </div>
</template>

<script>
export default {
    props: ["item","activeId"],
    emits:["onSelectedShareScreen"],
    data(){
        return {
            hover: false,
            viewStream: null
        };
    },
    mounted(){
        //this.setVideoSource()
    },
    unmounted(){
        this.viewStream = null
    },
    methods:{
        async onHover(){
            if(this.hover) return
            this.hover = true
            await this.setVideoSource()
        },
        async onLeave(){
            const self = this
            this.hover = false
            if(this.$refs.video.srcObject){
                this.$refs.video.srcObject.getVideoTracks().forEach(track=>{
                    track.stop();
                    this.$refs.video.srcObject.removeTrack(track);
                    self.viewStream = null
                });
            }
        },
        onSelect(){
            this.$emit("onSelectedShareScreen", this.item.id)
        },
        async setVideoSource(){
            return new Promise(async(resolve)=>{
                try {
                    if(this.viewStream) return
                    const stream = await navigator.mediaDevices.getUserMedia({
                        audio: false,
                        video: {
                            mandatory: {
                            chromeMediaSource: 'desktop',
                            chromeMediaSourceId: this.item.id,
                            minWidth: 160,
                            maxWidth: 160,
                            minHeight: 120,
                            maxHeight: 120
                            }
                        }
                    })
                    this.viewStream = stream
                } catch (e) {
                    console.log("error",e)
                }
                resolve()
            });
            
        }
    }
}
</script>

