<template>
    <div class="screen-share-container">
        <template v-for="item in items" :key="item.id">
            <div class="screen-share-item">
                <PreviewShareScreenItem :item="item" :activeId="activeId" @onSelectedShareScreen="onSelectedShareScreen"/>
            </div>
        </template>
    </div>
</template>

<script>
import PreviewShareScreenItem from './PreviewShareScreenItem.vue';
export default {
    components: {PreviewShareScreenItem},
    props:["items"],
    emits:["onSelectedShareScreen"],
    data(){
        return {
            activeId: null
        }
    },
    methods: {
        onSelectedShareScreen(sourceId){
            this.activeId = sourceId
            this.$emit("onSelectedShareScreen", sourceId)
        }
    }
}
</script>

