    .screen-share-container{
        height: 280px;
        width: 100%;
        overflow-y: auto;
        .screen-share-item{
            width: 150px;
            display: inline-block;
            margin: 5px;
            img{
                width: 100px;
                height: 100px;
            }
        }
    }
    
