<template>
    <div class="split split-horizontal" style="">
        <slot></slot>
    </div>
</template>

<script>
  import Emitter from './emitter';
  export default {
    name: 'SplitArea',
    mixins: [Emitter],
    props: {
      size: {
        type: Number,
        default: 50
      },
      minSize: {
        type: Number,
        default: 100
      }
    },
    data(){
        return {
            classes: `split split-horizontal`
        }
    },
    computed: {
        classes1() {
            return `split split-` + this.$parent.direction
        }
    },
    watch: {
        'size' (val) {
            this.$parent.changeAreaSize()
        },
        'minSize' (val) {
            this.$parent.changeAreaSize()
        }
    }
  };
</script>